import React, { useState } from "react";
import ToDoLists from "./TodoLists";
import axios from "axios";
const App=()=>{
    const [input, setData]=useState("");
    const [items, setItems]=useState([]);


    const dataEvent=(event)=>{       
            setData(event.target.value);
   };

   const ItemList = async() => {
            
    const {data, status }= await axios.post('http://localhost:8000/add/item', { data :input })
            .then((response) => {
                setItems((oldItems) => [...oldItems, data]);
                setData(data?.data);
            })
            .catch((error) => {
                console.error("There was an error adding the item!", error);
            });
    };

    const deleteItems = (id) => {
        axios.delete(`http://localhost:8000/add/item/${id}`)
            .then((response) => {
                setItems((oldItems) => oldItems.filter((items, index) => index !== id));
            })
            .catch((error) => {
                console.error("There was an error deleting the item!", error);
            });
    };
   return(
    <>
        <div className="main_div">
            <div className="center_div">
                <br />
                <h1 className="head">ToDo List</h1>
                <br />
                <input type="text" placeholder="Add Items" value={data} onChange={dataEvent}/>
                <button onClick={ItemList}> + </button>
                
                <ol>
                    {
                        items.map((itemval, index)=>{
                            return <ToDoLists 
                            key={index} 
                            id={index} 
                            text={itemval} 
                            onSelect={deleteItems}
                            />
                        })
                    } 
                </ol>
            </div>
        </div>
    </>
   );
};
export default App;
