   const ItemList=()=>{
    
   }
   return(
    <>
        <div className="main_div">
            <div className="center_div">
                <br />
                <h1 className="head">ToDo List</h1>
                <br />
                <input type="text" placeholder="Add Items" onChange={dataEvent}/>
                <button onClick={ItemList}> + </button>
                
                <ol>
                    <li> {data} </li>
                </ol>
            </div>
        </div>
    </>
   );
};
export default App;
